sln01b :: Int -> Int -> String -> Int
sln01b _ idx [] = idx
sln01b acc idx (h : t)
  | acc == (-1) = idx
  | h == '('    = sln01b (acc + 1) (idx + 1) t
  | otherwise   = sln01b (acc - 1) (idx + 1) t

